CREATE TABLE IF NOT EXISTS "SPACE_SHIP" (
  "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 100) NOT NULL PRIMARY KEY,
  "NAME" VARCHAR(50) NOT NULL,
  "MEDIA_SHOW" VARCHAR(255)
);

CREATE TABLE IF NOT EXISTS "SPACE_SHIP_EQUIPMENT" (
  "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 100) NOT NULL PRIMARY KEY,
  "SPACE_SHIP_ID" BIGINT NOT NULL,
  "SHIP_EQUIPMENT" VARCHAR(50) NOT NULL,
  FOREIGN KEY ("SPACE_SHIP_ID") REFERENCES "SPACE_SHIP"("ID")
);

CREATE TABLE IF NOT EXISTS "AUTH_USER"(
    "ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 100) NOT NULL PRIMARY KEY,
    "PASSWORD" VARCHAR(512),
    "ROLE" VARCHAR(50),
    "USERNAME" VARCHAR(50) NOT NULL UNIQUE
);
